// for goby::core::ApplicationBase
#include "goby/core/core.h"

// autogenerated Protocol Buffers header
#include "hello_world.pb.h"

// create our Goby Application with ApplicationBase as a public base
class HelloWorld1 : public goby::core::ApplicationBase
{
private:
    // loop() is a virtual method of ApplicationBase that is called
    // at 10 Hz (by default)
    void loop()
        {
            static int i = 0;
            // create a message of type HelloWorldMsg (defined in hello_world.proto)
            HelloWorldMsg msg;
            // set the fields we need
            msg.set_telegram("hello world!");
            msg.set_count(++i);

            glogger() << "sending: " << msg << std::endl;

            // publish it to `gobyd` who will send to all subscribers 
            publish(msg);
        }
};

int main(int argc, char* argv[])
{
    // start up our application (ApplicationBase will read argc and argv for us)
    return goby::run<HelloWorld1>(argc, argv);
}
